mapper.select:
select id_ from dg_currency_exchange


			and foreign_currency_code=#{cm.foreignCurrencyCode}


			and rate=#{cm.rate}


		and (
			or foreign_currency_code like CONCAT('%',#{cm.keyword},'%')
			or rate like CONCAT('%',#{cm.keyword},'%')
		)



	order by ${cm.orderBy}


	${cm.sortAsc}
